class Sekil 
    def bilgi 
        puts "kare ---> KARE/kare"
        puts "daire ---> DAİRE/daire"
        puts "küp ---> KÜP/küp"
        puts "küre ---> KÜRE/küre"
        puts "çıkmak ---> ÇIKMAK/cikis"
        puts "-------------"
    end
end
class IkiBoyutluSekil < Sekil
    def alan_hesapla
        puts ".........."
        puts "(İKİ BOYUTLU , KARE şekli oluşturuldu)"
        puts ".........."
    end
    def alan_hesapla1
        puts ".........."
        puts "(İKİ BOYUTLU , DAİRE şekli oluşturuldu)"
        puts ".........."
    end
end
class UcBoyutluSekil < IkiBoyutluSekil
    def alan_hesapla2
        puts ".........."
        puts "(ÜÇ BOYUTLU , KÜP şekli oluşturuldu)"
        puts ".........."
    end
    def alan_hesapla3
        puts ".........."
        puts "(ÜÇ BOYUTLU , KÜRE şekli oluşturuldu)"
        puts ".........."
    end
end
class Kare < IkiBoyutluSekil
    def alan_hesapla   
    print "ŞEKLİ GİRİNİZ:"
    sekil = gets
    print "Kenar uzunluğu giriniz:"
    kenar = gets.to_i
    super()
    alan = kenar*kenar
    puts "ALANI : #{alan}"
    puts "------------"
    end
end
class Daire < IkiBoyutluSekil
    def alan_hesapla1
        print "SEKLİ GİRİNİZ:"
        sekil = gets
        print "Yarıçapı giriniz:"
        yarıçap = gets.to_i
        super()
        π = 3.
        alan = π*yarıçap*yarıçap
        puts "ALANI: #{alan}"
        puts "------------"
    end
end
class Kup < UcBoyutluSekil
     def alan_hesapla2
        print "SEKLİ GİRİNİZ:"
        sekil = gets
        print "Kenar uzunluğu giriniz:"
        kenar = gets.to_i
        super()
        alan = 6*kenar*kenar
        puts "ALANI: #{alan}"
        
    end
    def hacim_hesapla
        print "Kenar uzunluğunu tekrar giriniz:"
        kenar = gets.to_i
        hacim = kenar*kenar*kenar
        puts "HACMİ : #{hacim}"
        print "Hacim artış değerini giriniz:"
        hacim_artıs = gets.to_i
        yeni_hacim = hacim*hacim_artıs
        puts "YENİ HACMİ: #{yeni_hacim}"
        puts "------------"
    end
end
class Kure < UcBoyutluSekil
    def alan_hesapla3
        print "SEKLİ GİRİNİZ:"
        sekil = gets
        print "Yarıçapı giriniz:"
        yarıçap = gets.to_i
        super()
        π = 3.
        alan = 4*π*yarıçap*yarıçap
        puts "ALANI : #{alan}"
    end
    def hacim_hesapla
        print "Yarıçapı tekrar giriniz:"
        yarıçap = gets.to_i
        π = 3.
        hacim = 1.333333333333*π*yarıçap*yarıçap*yarıçap
        puts "HACMİ: #{hacim}"
    end
    def hacim_arttırma
        print "Yarıçapı tekrar giriniz:"
        yarıçap = gets.to_i
        print "Hacim artış değerini giriniz:"
        hacim_artıs = gets.to_i
        π = 3.
        hacim =  1.333333333333*π*yarıçap*yarıçap*yarıçap
        yeni_hacim = hacim*hacim_artıs
        puts "YENİ HACMİ: #{yeni_hacim}"
        puts "------------"
    end
end
    sekil1 = Sekil.new()
